var Ajax={Run:function(c,b,a){$.ajax({url:c,type:"POST",data:b,dataType:"json",cache:false,async:true,success:function(d){return a(d)
}})
},Error:function(b){$(".form p.error").html("");
$(".form span.error").html("");
for(i=0;
i<b.Errors.length;
i++){var a=b.Errors[i];
if(a.Id!=""){$("#"+a.Id).next("span.error").html(a.Message).fadeTo("slow",1)
}else{$(".form p.error").append(a.Message+"<br />")
}}$(".form p.error").fadeTo("slow",1)
},Clear:function(){$(".form p.error").fadeTo("fast",0);
$(".form p.error").html("");
$(".form span.error").fadeTo("fast",0);
$(".form span.error").html("")
},Init:function(){Ajax.Clear();
var b=$(".container h1:first").position();
var a=b.left-30;
var c=b.top+10;
$("#ajaxProcessing").css("left",a+"px").css("top",c+"px").fadeIn("fast")
},Stop:function(){$("#ajaxProcessing").fadeOut("fast")
},SetUploader:function(b,e){var a="/Projects/Resources/Content/js/lib/tools/ajaxupload/";
var c=a+"jquery.blockUI.min.js";
var d=a+"jquery.form.min.js";
Load.JS(d,true,function(){Load.JS(c,true,function(){var f=$("#"+f);
var g=$("#"+g);
f.ajaxForm({iframe:true,type:"post",dataType:"json",clearForm:true,complete:function(j){var h=j.responseText.replace("<textarea>","").replace("</textarea>","");
h=$.parseJSON(h);
if(h.Success){$.growlUI(null,"Success uploading file");
g.val(h.Result.Path).show("slow")
}else{$.growlUI(null,"Error uploading file");
g.next("span.error").html(h.Errors[0].Message).fadeTo("slow",1)
}f.unblock();
f.resetForm()
},beforeSubmit:function(){g.next("span.error").fadeTo("fast",0).html("");
f.block({message:'<h6><img src="/Projects/Resources/Content/img/ajax-loader.gif" />Uploading file...</h6>'})
}})
})
})
}};