var delim=",";
var tabSavePath="/API/Engine/SaveTabOrder/";
var sectionSavePath="/API/Engine/SaveSectionOrder/";
var linkSavePath="/API/Engine/SaveLinkOrder/";
var accordionSavePath="/API/Engine/SaveAccordionItemsOrder/";
var Sort={Open:function(a,b,c){$("#"+b).css("left",$(a).position().left);
$("#"+b).css("top",$(a).position().top+20);
$("#"+b).show();
$("#"+b+" div.header").html(c)
},Close:function(a){$("#"+a).hide()
},Up:function(a){var c=$("#"+a+" option:selected");
var b=c.prev("option");
if($(b).text()!=""){$(c).remove();
$(b).before($(c))
}},Down:function(a){var c=$("#"+a+" option:selected");
var b=c.next("option");
if($(b).text()!=""){$(c).remove();
$(b).after($(c))
}},Save:function(a,d,b,c){switch(b){case"tab":Sort.SaveTabs(a,d);
break;
case"section":Sort.SaveSections(c,a,d);
break;
case"link":Sort.SaveLinks(c,a,d);
break;
case"accordion":Sort.SaveAccordions(c,a,d);
break
}},GetOrder:function(b,a){var d="";
var c=$("#"+b+" option");
c.each(function(e){d+=a+$(this).val()
});
d=((d.length>=1)?d.substr(1):d);
return d
},SaveTabs:function(a,c){var b=Sort.GetOrder(a,delim);
Ajax.Run(tabSavePath,{order:b,delim:delim},function(d){if(d.Success){Sort.Close(c);
Page.Reload("div.actionBox")
}else{console.log(d.Error)
}})
},SaveSections:function(c,a,d){var b=Sort.GetOrder(a,delim);
Ajax.Run(sectionSavePath,{tabId:c,order:b,delim:delim},function(e){if(e.Success){Sort.Close(d);
Page.Reload("div.actionBox")
}else{console.log(e.Error)
}})
},SaveLinks:function(c,a,d){var b=Sort.GetOrder(a,delim);
Ajax.Run(linkSavePath,{sectionId:c,order:b,delim:delim},function(e){if(e.Success){Sort.Close(d);
Page.Reload("div.actionBox")
}else{console.log(e.Error)
}})
},SaveAccordions:function(c,a,d){var b=Sort.GetOrder(a,delim);
Ajax.Run(accordionSavePath,{pageId:c,order:b,delim:delim},function(e){if(e.Success){Sort.Close(d);
Page.Reload("div.actionBox")
}else{console.log(e.Error)
}})
}};